import * as echarts from 'echarts/core';
import { TitleComponent, TooltipComponent } from 'echarts/components';
import { SankeyChart } from 'echarts/charts';
import { CanvasRenderer } from 'echarts/renderers';

// 注册必要的组件
echarts.use([TitleComponent, TooltipComponent, SankeyChart, CanvasRenderer]);
export default {
  name: 'SangJiTu',
  props: {
    sankeyData: {
      type: Array,
      default: () => []
    }
  },
  watch: {
    sankeyData: 'initChart'
  },
  mounted() {
    this.initChart();
  },
  methods: {
    initChart() {
      if (!this.sankeyData || this.sankeyData.length === 0) {
        return;
      }
      var chartDom = document.getElementById('main');
      var myChart = echarts.init(chartDom);
      myChart.showLoading();

      // 解析传入的数据并生成符合 ECharts 数据格式的结构
      const nodes = [];
      const links = [];
      const nodeSet = new Set();

      // 为桑基图节点和链接生成数据
      this.sankeyData.forEach((item, index) => {
        item.nodes.forEach((node, nodeIndex) => {
          if (!nodeSet.has(node)) {
            nodes.push({
              name: node
            });
            nodeSet.add(node);
          }
        });
        if (index > 0) {
          links.push({
            source: this.sankeyData[index - 1].nodes[0],
            // 连接前一个节点的第一个节点
            target: item.nodes[0],
            value: 1 // 用于示意的权重，视数据情况调整
          });
        }
      });
      myChart.hideLoading();
      myChart.setOption({
        title: {
          text: '家族迁徙桑基图',
          subtext: '基于姓氏和迁居地'
        },
        tooltip: {
          trigger: 'item',
          triggerOn: 'mousemove'
        },
        series: [{
          type: 'sankey',
          data: nodes,
          links: links,
          emphasis: {
            focus: 'adjacency'
          },
          levels: [{
            depth: 0,
            itemStyle: {
              color: '#fbb4ae'
            },
            lineStyle: {
              color: 'source',
              opacity: 0.6
            }
          }, {
            depth: 1,
            itemStyle: {
              color: '#b3cde3'
            },
            lineStyle: {
              color: 'source',
              opacity: 0.6
            }
          }, {
            depth: 2,
            itemStyle: {
              color: '#ccebc5'
            },
            lineStyle: {
              color: 'source',
              opacity: 0.6
            }
          }, {
            depth: 3,
            itemStyle: {
              color: '#decbe4'
            },
            lineStyle: {
              color: 'source',
              opacity: 0.6
            }
          }],
          lineStyle: {
            curveness: 0.5
          }
        }]
      });
    }
  }
};
